    /*Botón de VER CONTACTOS
    */
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        String contenido = ManejoArchivos.leerArchivo();
        jTextArea1.setText(contenido);
        
    }//GEN-LAST:event_jButton1ActionPerformed

    /*Botón de CREAR NUEVOS CONTACTOS
    */
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        
    // Obtener valores de los cajones
    String nombre = jTextField2.getText();
    String numero = jTextField1.getText();

    // Verificar que ambos cajones no estén vacíos
    if (!nombre.isEmpty() && !numero.isEmpty()) {
        // Unir nombre y número
        String contacto = nombre + "!" + numero;

    // Método para escribir el contacto en el archivo
        ManejoArchivos.escribirContacto(contacto);

    // Limpiar los campos después de agregar el contacto
        jTextField2.setText("");
        jTextField1.setText("");
    } else {
        System.out.println("Ingrese tanto el nombre como el número antes de agregar el contacto.");
    }       
    }//GEN-LAST:event_jButton2ActionPerformed

    /*Botón de ELIMINAR CONTACTO
    */
    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed

    // Obtener valores de cajones
    String nombre = jTextField2.getText();
    String nuevoNumero = jTextField1.getText();

    // Verificar que ambos cajones no estén vacíos
    if (!nombre.isEmpty()) {
        // Método para eliminar el contacto en el archivo
        ManejoArchivos.eliminarContacto(nombre, nuevoNumero);

        // Limpiar los campos después de eliminar el contacto
        jTextField2.setText("");
        jTextField1.setText("");
    } else {
        System.out.println("Ingrese el nombre antes de eliminar el contacto.");
    }
        
    }//GEN-LAST:event_jButton4ActionPerformed

