    /*Botón de ACTUALIZAR CONTACTO
    */
    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

    // Obtener valores de los cajones    
        String nombre = jTextField2.getText();
        String numero = jTextField1.getText();

    // Verificar que ambos cajones no estén vacíos
    if (!nombre.isEmpty() && !numero.isEmpty()) {
        // Escribir el contacto actualizado en el archivo
        ManejoArchivos.actualizarContacto(nombre, numero);

        // Limpiar los campos después de actualizar
        jTextField2.setText("");
        jTextField1.setText("");
    } else {
        System.out.println("Ingrese tanto el nombre como el número antes de actualizar el contacto.");
    }
    }//GEN-LAST:event_jButton3ActionPerformed

    /*MOVIMIENTO PARA LA VENTANA CON EL MOUSE
    */
    private void jPanel2MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPanel2MousePressed
//Guardamos la posición del mouse para mover la barra de encima        
    xMouse = evt.getX();
    yMouse = evt.getY();
    }//GEN-LAST:event_jPanel2MousePressed

    private void jPanel2MouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPanel2MouseDragged
//Posición del Mouse en la pantalla
        int x = evt.getXOnScreen();
        int y = evt.getYOnScreen();
//Configuramos el evento cuando el mouse es arrastrado
        this.setLocation(x - xMouse,y - yMouse);
    }//GEN-LAST:event_jPanel2MouseDragged

    private void jTextField2MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTextField2MousePressed
//Quitamos el texto al usuario presionar sobre el cajón
        jTextField2.setText("");
    }//GEN-LAST:event_jTextField2MousePressed

